import { makeStyles } from '@material-ui/core/styles';
import React , {useState} from 'react';
import photo from './profile_photo.png';
import DashboardIcon from '@material-ui/icons/Dashboard';
import ViewAgendaIcon from '@material-ui/icons/ViewAgenda';
import RadioButtonUncheckedIcon from '@material-ui/icons/RadioButtonUnchecked';
const [requests, setRequests] = useState(false);
function App(){
  const classes = useStyles();
  return (
      <div className={classes.container}>
        <div className={classes.card} >
          <span className={classes.title}>xo.</span>
          <div className={classes.rowContainer}>
             <img src={photo}/> 
             <div className={classes.nameContainer}>
             <span>Ahmed Reda</span>
             <span className={classes.id}>#1253724</span>
             </div>
          </div>
          <div className={classes.rowContainer}>
            <DashboardIcon/>
            <span style={{marginLeft: 20}}>Dashboard</span>
          </div>
          <div className={classes.rowContainer} onClick={handleRequests()}>
            <ViewAgendaIcon/>
            <span style={{marginLeft: 20}}>Requests</span>
            </div>
            {requests && 
            <div className={classes.subRow}>
              <RadioButtonUncheckedIcon/>
              <span>Pending</span>
            </div>
            }
          </div>
      </div>
  );
}
const handleRequests = () => {
  if (requests) {
    setRequests(false);
  }
  else {
    setRequests(true);
  }
}
const useStyles = makeStyles({
  container: {
    backgroundColor: 'white',
  },
  card: {
    display: 'flex',
    flexDirection: 'column',
    backgroundColor: 'white',
    boxShadow: '0px 4px 4px rgba(0, 0, 0, 0.25)',
    width: 256,
    height: 950,
  },
  title: {
    color: '#0A47C4',
    marginLeft: '24px',
    fontWeight: 'bold',
    fontSize: '24px',
  },
  rowContainer: {
    display: 'flex',
    marginLeft: 10,
    marginTop: 20,
    flexDirection: 'row',
  },
  subRow : {
    display: 'flex',
    marginLeft: 20,
    marginTop: 20,
    flexDirection: 'row',
  },
  id: {
    color: '#90A0B7',
  },
  nameContainer: {
    marginLeft: 20,
    display: 'flex',
    flexDirection: 'column',
  },
});
export default App;
